mapper.select:
SELECT cr.chatroom_no as "chatroom.chatroomNo", cr.create_at as "chatroom.createAt", cr.user_no as "chatroom.userNo", cr.gosu_no as "chatroom.gosuNo", M.user_name as "userName", M.USER_PROFILE as "userProfile", C.CHAT_NO AS "chat.chatNo", C.CONTENT as "chat.content", C.TYPE as "chat.type", C.CHATROOM_NO as "chat.chatroomNo", (SELECT COUNT(*) FROM CHAT WHERE CHAT_READ = 0 AND CHAT.CHATROOM_NO = CR.CHATROOM_NO AND SENDER != ${userNo}) as unReadCnt FROM CHATROOM cr LEFT JOIN ( SELECT chat_no, content, type, CHATROOM_NO FROM CHAT where (chat_no, CHATROOM_NO) in ( SELECT MAX(CHAT_NO) AS CHAT_NO, CHATROOM_NO FROM CHAT GROUP BY CHATROOM_NO ) )C ON cr.CHATROOM_NO = c.CHATROOM_NO
LEFT JOIN ( SELECT USER_NAME, USER_NO, USER_PROFILE FROM MEMBER ) M ON CR.GOSU_NO = M.USER_NO WHERE CR.USER_NO = #{userNo}
LEFT JOIN ( SELECT USER_NAME, USER_NO, USER_PROFILE FROM MEMBER ) M ON CR.USER_NO = M.USER_NO WHERE CR.GOSU_NO = #{userNo}

mapper.update:
UPDATE CHAT
SET CHAT_READ = 1
WHERE CHATROOM_NO = ${chatroomNo} AND SENDER != ${sender}

mapper.select:
select CATEGORY_SMALL_NAME
from SERVICE_CATEGORY_SMALL
where CATEGORY_SMALL_NO = #{categorySmallNo}

mapper.update:
UPDATE ESTIMATE
SET STATUS = #{status}
WHERE EST_NO = #{estNo}

UPDATE ESTIMATE
SET STATUS = 1
WHERE USER_NO = ${userNo} AND GOSU_NO = ${gosuNo}

mapper.select:
SELECT gosu_no as gosuNo, introduction, employees, business_status as businessStatus, education, career, elaborate, region, move_distance as moveDistance, available_time as availableTime, user_no
FROM GOSU
WHERE USER_NO = ${userNo}

SELECT gosu_img_no as gosuImgNo, gosu_img_url as gosuImgUrl, status as status, gosu_no as gosuNo
FROM "GOSU-IMG"
WHERE GOSU_NO = ${userNo}

SELECT NVL(AVG(REV_POINT), 0) AS AVERAGE_REV_POINT
FROM REVIEW R
WHERE R.GOSU_NO = ${userNo}

select *
from CHATROOM
where USER_NO = ${userNo} and GOSU_NO = ${gosuNo}
