			   , ELABORATE
			   , REGION
			   , MOVE_DISTANCE
			   , AVAILABLE_TIME
			   , USER_NO)
		  VALUES(#{gosuNo}
		  	   , #{introduction}
		  	   , #{employees}
		  	   , #{businessStatus}
		  	   , #{education}
		  	   , #{career}
		  	   , #{elaborate}
		  	   , #{region}
		  	   , #{moveDistance}
		  	   , #{availableTime}
		  	   , #{userNo})

mapper.update:
UPDATE MEMBER
   SET USER_STATUS = 2
 WHERE USER_NO = #{userNo}
